import { Card } from "@mui/material";
import axios from "axios";
import { Button } from "primereact/button";
import { Column } from "primereact/column";
import { DataTable } from "primereact/datatable";
import { Dialog } from "primereact/dialog";
import React, { useEffect, useState } from "react";
import { API_URL } from "../constants/constants";
import { Layout } from "./Layout";

export const Famille = () => {

    const [famille, setFamille] = useState([]);
    const [visibilityEdit, setVisibilityEdit] = useState(false);
    const [visibilityDelete, setVisibilityDelete] = useState(false);
    const [visibilityAdd, setVisibilityAdd] = useState(false);

    let emptyFamille = {
        id: null,
        firstname: '',
        lastname: '',
        contact: null,
        address: 0,
    };

    useEffect(() => {
        if(localStorage.getItem('access_token') === null){                   
            window.location.href = '/login'
        }
        else{
         (async () => {
           try {
            const token = localStorage.getItem('access_token')
             const {data} = await axios.get(   
                            API_URL + 'famille/', {
                            headers: {
                                Authorization: `Bearer ${token}`
                                }}
                           );
             setFamille(data);
          } catch (e) {
            console.log('not auth'+ e)
          }
         })()};
    }, []);

    const editFamille = (famille) => {
        setFamille({ ...famille });
        setVisibilityEdit(true);
    };

    const confirmDeleteFamille = (famille) => {
        setFamille(famille);
        setVisibilityDelete(true);
    };
    const actionBodyTemplate = (rowData) => {
        return (
            <React.Fragment>
                <Button icon="pi pi-pencil" rounded outlined className="mr-2" onClick={() => editFamille(rowData)} />
                <Button icon="pi pi-trash" rounded outlined severity="danger" onClick={() => confirmDeleteFamille(rowData)} />
            </React.Fragment>
        );
    };

    const footerContent = () => (
        <div>
            <Button label="No" icon="pi pi-times" onClick={() => setVisibilityAdd(false)} className="p-button-text" />
            <Button label="Yes" icon="pi pi-check" onClick={() => setVisibilityAdd(false)} autoFocus />
        </div>
    );

    const openNew = () => {
        setFamille(emptyFamille);
        setVisibilityAdd(true);
    };

    const hideAdd = () => {
        setVisibilityAdd(false);
    }
    return (
        <Layout>

            <div className="head">
                <Button label="Nouveau famille" icon="pi pi-plus" onClick={openNew}/>
            </div>
            <DataTable value={famille} tableStyle={{ minWidth: '50rem' }}>
                <Column field="firstname" header="Nom"></Column>
                <Column field="lastname" header="Famille"></Column>
                <Column field="contact" header="Contact"></Column>
                <Column field="address" header="Adresse"></Column>
                <Column body={actionBodyTemplate} exportable={false} style={{ minWidth: '12rem' }}></Column>

            </DataTable>

            <Dialog header="Header" visible={visibilityAdd} style={{ width: '50vw' }} onHide={hideAdd} footer={footerContent}>
                <p className="m-0">
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. 
                    Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo
                    consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. 
                    Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
                </p>
            </Dialog>

        </Layout>
      );
  }
