import axios from "axios";
import { Button } from "primereact/button";
import { Column } from "primereact/column";
import { DataTable } from "primereact/datatable";
import { Dialog } from "primereact/dialog";
import React, { useEffect, useState } from "react";
import { API_URL } from "../constants/constants";
import { Layout } from "./Layout";
import { classNames } from 'primereact/utils';


export const Famille = () => {

    const [famille, setFamille] = useState([]);
    const [visibilityEdit, setVisibilityEdit] = useState(false);
    const [visibilityDelete, setVisibilityDelete] = useState(false);
    const [visibilityAdd, setVisibilityAdd] = useState(false);

    let emptyFamille = {
        id: null,
        firstname: '',
        lastname: '',
        contact: null,
        address: 0,
    };

    useEffect(() => {
        if(localStorage.getItem('access_token') === null){                   
            window.location.href = '/login'
        }
        else{
         (async () => {
           try {
            const token = localStorage.getItem('access_token')
             const {data} = await axios.get(   
                            API_URL + 'famille/', {
                            headers: {
                                Authorization: `Bearer ${token}`
                                }}
                           );
             setFamille(data);
          } catch (e) {
            console.log('not auth'+ e)
          }
         })()};
    }, []);

    const editFamille = (famille) => {
        setFamille({ ...famille });
        setVisibilityEdit(true);
    };

    const confirmDeleteFamille = (famille) => {
        setFamille(famille);
        setVisibilityDelete(true);
    };
    const actionBodyTemplate = (rowData) => {
        return (
            <React.Fragment>
                <Button icon="pi pi-pencil" rounded outlined className="mr-2" onClick={() => editFamille(rowData)} />
                <Button icon="pi pi-trash" rounded outlined severity="danger" onClick={() => confirmDeleteFamille(rowData)} />
            </React.Fragment>
        );
    };

    const footerContent = () => (
        <div>
            <Button label="No" icon="pi pi-times" onClick={() => setVisibilityAdd(false)} className="p-button-text" />
            <Button label="Yes" icon="pi pi-check" onClick={() => setVisibilityAdd(false)} autoFocus />
        </div>
    );

    const openNew = () => {
        setFamille(emptyFamille);
        setVisibilityAdd(true);
    };

    const hideAdd = () => {
        setVisibilityAdd(false);
    }
    return (
        <Layout>

            <div className="head">
                <Button label="Nouveau famille" icon="pi pi-plus" onClick={openNew}/>
            </div>
            <DataTable value={famille} tableStyle={{ minWidth: '50rem' }}>
                <Column field="firstname" header="Nom"></Column>
                <Column field="lastname" header="Famille"></Column>
                <Column field="contact" header="Contact"></Column>
                <Column field="address" header="Adresse"></Column>
                <Column body={actionBodyTemplate} exportable={false} style={{ minWidth: '12rem' }}></Column>

            </DataTable>

            <Dialog visible={visibilityAdd} style={{ width: '32rem' }} breakpoints={{ '960px': '75vw', '641px': '90vw' }} header="Nouveau famille" modal className="p-fluid" footer={footerContent} onHide={hideAdd}>
                    <div className="field">
                        <label htmlFor="name" className="font-bold">
                            Firtname
                        </label>
                        <InputText id="name" value={personne.firstname} onChange={(e) => onInputChange(e, 'name')} required autoFocus className={classNames({ 'p-invalid': submitted && !personne.firstname })} />
                        {submitted && !personne.firstname && <small className="p-error">Name is required.</small>}
                    </div>
                    <div className="field">
                        <label htmlFor="description" className="font-bold">
                            Lastname
                        </label>
                        <InputTextarea id="description" value={personne.lastname} onChange={(e) => onInputChange(e, 'description')} required rows={3} cols={20} />
                    </div>

                    <div className="field">
                        <label className="mb-3 font-bold">Feu</label>
                        <div className="formgrid grid">
                            <div className="field-radiobutton col-6">
                                <RadioButton inputId="category1" name="category" value="Accessories" onChange={onCategoryChange} checked={personne.feu === 1} />
                                <label htmlFor="category1">Vivant(e)</label>
                            </div>
                            <div className="field-radiobutton col-6">
                                <RadioButton inputId="category2" name="category" value="Clothing" onChange={onCategoryChange} checked={personne.feu === 0} />
                                <label htmlFor="category2">Mort(e)</label>
                            </div>
                        </div>
                    </div>

                    <div className="formgrid grid">
                        <div className="field col">
                            <label htmlFor="age" className="font-bold">
                                Age
                            </label>
                            <InputNumber id="age" value={personne.age} onValueChange={(e) => onInputNumberChange(e, 'age')} mode="currency" currency="USD" locale="en-US" />
                        </div>
                        <div className="field col">
                            <label htmlFor="quantity" className="font-bold">
                                Adresse
                            </label>
                            <InputNumber id="quantity" value={personne.address} onValueChange={(e) => onInputNumberChange(e, 'quantity')} />
                        </div>
                    </div>
                </Dialog>

        </Layout>
      );
  }
